import React, { useState, useEffect } from 'react';
import PropertyList from './components/PropertyList';
import PropertyForm from './components/PropertyForm';
import './App.css';

function App() {
  const [properties, setProperties] = useState([]);
  const [editingProperty, setEditingProperty] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/properties');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setProperties(data);
      setError(null);
    } catch (error) {
      console.error('Error fetching properties:', error);
      setError(`Failed to load properties: ${error.message}. Make sure the backend server is running on port 5000.`);
    } finally {
      setLoading(false);
    }
  };

  const handlePropertySave = () => {
    fetchProperties();
    setEditingProperty(null);
  };

  const handlePropertyDelete = async (id) => {
    try {
      const response = await fetch(`/api/properties/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) {
        throw new Error('Failed to delete property');
      }
      fetchProperties();
    } catch (error) {
      console.error('Error deleting property:', error);
      setError('Failed to delete property');
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>üè¢ Property Management System</h1>
        <p>Manage your properties efficiently</p>
      </header>
      <main className="main-content">
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
        
        <PropertyForm 
          property={editingProperty} 
          onSave={handlePropertySave}
          onCancel={() => setEditingProperty(null)}
        />
        
        {loading ? (
          <div className="loading">Loading properties...</div>
        ) : (
          <PropertyList 
            properties={properties} 
            onEdit={setEditingProperty}
            onDelete={handlePropertyDelete}
          />
        )}
      </main>
    </div>
  );
}

export default App;
